import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import toast from "react-hot-toast";

function CreateResume() {
  const [inputs, setInputs] = useState({
    name: "",
    email: "",
    experiences: [],
    education: [],
    skills: [],
  });

  const [experienceInputs, setExperienceInputs] = useState({});
  const [educationInputs, setEducationInputs] = useState({});
  const [skillsInputs, setSkillsInputs] = useState({});

  const [experiences, setExperiences] = useState([]);
  const [education, setEducation] = useState([]);
  const [skillState, setSkillState] = useState([]);

  const handleChange = (e) => {
    setInputs((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value,
    }));
  };

  const handleExperienceChange = (e) => {
    setExperienceInputs((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value,
    }));
  };

  const handleEducationChange = (e) => {
    setEducationInputs((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value,
    }));
  };

  const handleSkillsChange = (e) => {
    setSkillsInputs((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value,
    }));
  };

  const handleExperience = (e) => {
    e.preventDefault();
    setExperiences([...experiences, experienceInputs]);
    setExperienceInputs({}); // Clear the input fields
  };

  const handleEducation = (e) => {
    e.preventDefault();
    setEducation([...education, educationInputs]);
    setEducationInputs({}); // Clear the input fields
  };

  const handleSkills = (e) => {
    e.preventDefault();
    setSkillState([...skillState, skillsInputs]);
    setSkillsInputs({}); // Clear the input fields
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const dataToSubmit = {
      ...inputs,
      experiences,
      education,
      skills: skillState,
    };
    try {
      const { data } = await axios.post(
        "http://localhost:3000/product",
        dataToSubmit
      );
      if (data.success) {
        toast.success("Resume submitted successfully");
      }
    } catch (error) {
      console.error(error);
    }
  };
  // console.log(updatedExperience);

  return (
    <div>
      <nav className="navbar bg-body-tertiary shadow mb-3">
        <div className="container">
          <a className="navbar-brand" href="#">
            Resume Builder
          </a>
          <div>
            <a href="http://localhost:3000/profile">
              <button className="btn btn-sm btn-dark">
                <i className="bi bi-person-circle" /> My Profile
              </button>
            </a>

            <button className="btn btn-sm btn-danger">
              <i className="bi bi-box-arrow-left" /> Logout
            </button>
          </div>
        </div>
      </nav>
      <div className="container back-glass-login">
        <div className=" rounded shadow p-2 mt-4" style={{ minHeight: "80vh" }}>
          <div className="d-flex justify-content-between border-bottom">
            <h5>Create Resume</h5>
            <div>
              <a
                href="http://localhost:3000/resumelisting"
                className="text-decoration-none"
              >
                <i className="bi bi-arrow-left-circle" /> Back
              </a>
            </div>
          </div>
          <div>
            {/* form starts */}
            <form className="row g-3 p-3" onSubmit={handleSubmit}>
              <h5 className="mt-3 ">
                <i className="bi bi-person-badge" /> Personal Information
              </h5>
              {/* full name */}
              <div className="col-md-6">
                <label className="form-label">Full Name</label>
                <input
                  type="text"
                  placeholder="Dev Ninja"
                  className="form-control"
                  value={inputs.name}
                  onChange={handleChange}
                />
              </div>
              {/* email */}
              <div className="col-md-6">
                <label className="form-label">Email</label>
                <input
                  type="email"
                  placeholder="dev@abc.com"
                  className="form-control"
                  value={inputs.email}
                  onChange={handleChange}
                />
              </div>

              {/* mobile number */}
              <div className="col-md-6">
                <label className="form-label">Mobile No</label>
                <input
                  type="number"
                  min={1111111111}
                  placeholder={9569569569}
                  max={9999999999}
                  className="form-control"
                  value={inputs.number}
                  onChange={handleChange}
                />
              </div>

              {/* DOB */}
              <div className="col-md-6">
                <label className="form-label">Date Of Birth</label>
                <input
                  type="date"
                  className="form-control"
                  value={inputs.DOB}
                  onChange={handleChange}
                />
              </div>

              {/* Gender */}
              <div className="col-md-6">
                <label className="form-label">Gender</label>
                <select
                  className="form-select"
                  value={inputs.gender}
                  onChange={handleChange}
                >
                  <option>Male</option>
                  <option>Female</option>
                  <option>Transgender</option>
                </select>
              </div>

              {/* religion */}
              <div className="col-md-6">
                <label className="form-label">Religion</label>
                <select
                  className="form-select"
                  value={inputs.religion}
                  onChange={handleChange}
                >
                  <option>Hindu</option>
                  <option>Muslim</option>
                  <option>Sikh</option>
                  <option>Christian</option>
                </select>
              </div>

              {/* nationality */}
              <div className="col-md-6">
